// types
import types from './types';

// initial state
let initialState = {
    dialogsData: [
        { id: 1, name: 'Ванес' },
        { id: 2, name: 'Валера' },
        { id: 3, name: 'Димас' },
        { id: 4, name: 'Гена' },
        { id: 5, name: 'Лёха' }
    ],

    messagesData: [
        { message: 'YO YO YO' },
        { message: 'URA URA URA' },
        { message: 'Mother Fuck' }
    ]
}

export default function reducer(state = initialState, action) {

    switch (action.type) {

        case types.ADD_MESSAGE_DIALOGS: {
            return {
                ...state,
                messagesData: [...state.messagesData, { message: action.message }]
            }
        }

        default:
            return state
    }
}
