import React from 'react';
import { useDispatch } from 'react-redux';
import PropTypes from 'prop-types';
import cn from 'classnames';

// actions
import { getUsersThunk } from '../../state/users/actions';

import styles from './styles.module.css';

function Paginator({ className, totalCount, pageSize, currentPage }) {
    console.log('totalCount', totalCount);
    console.log('pageSize', pageSize);
    console.log('currentPage', currentPage);

    const dispatch = useDispatch();

    const onPageChanged = (pageNumber) => {
        dispatch(getUsersThunk(pageNumber, pageSize));
    }

    const pageCount = Math.ceil(totalCount / pageSize);
    const portion = 10;

    const pages = [];

    for (let i = 1; i <= pageCount; i++) {
        pages.push(i);
    }

    return (
        <ul className={cn(styles.Root, className)}>
            <button>prev</button>
            {
                pages.map((p, id) => {
                    return <li key={id} className={
                        currentPage === p ? styles.Root__item + ' ' +
                            styles.Root__item_selected :
                            styles.Root__item}
                        onClick={() => {
                            onPageChanged(p)
                        }}>{p}</li>
                })
            }
            <button>next</button>
        </ul>
    )
}

Paginator.propTypes = {
    className: PropTypes.string,
    totalCount: PropTypes.number,
    pageSize: PropTypes.number,
    currentPage: PropTypes.number,
}

Paginator.defaultProps = {
    className: '',
    totalCount: 0,
    pageSize: 0,
    currentPage: 0,
}

export default Paginator;
