import React, { useEffect, useCallback } from 'react';
import PropTypes from 'prop-types';
import { useSelector, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { compose } from 'redux';

// hoc
import { WithAuthRedirect } from '../../hoc/withAuthRedirect';

// actions
import {
    getProfileThunk,
    getUserStatusThunk
} from '../../state/profile/actions';

// components
import MyPosts from './MyPosts/MyPosts';
import Info from './Info/Info';

function Profile({ match }) {
    const dispatch = useDispatch();
    const autorizedUserId = useSelector(state => state.auth.userId);
    const profile = useSelector(state => state.profile.profile);
    const status = useSelector(state => state.profile.status);

    const handleUserId = useCallback(() => {
        let userId = match.params.userId;

        if (!userId) {
            userId = autorizedUserId;
        }

        dispatch(getProfileThunk(userId));
        dispatch(getUserStatusThunk(userId));
    }, [autorizedUserId, dispatch, match.params.userId])

    useEffect(() => {
        handleUserId();
    }, [handleUserId]);

    if(!profile) {
        return <Preloader />
    }

    return (
        <div>
            <Info profile={profile} status={status} />

            <MyPosts />
        </div>
    )
}

Profile.propTypes = {
    match: PropTypes.object.isRequired,
}

export default compose(
    withRouter,
    WithAuthRedirect
)(Profile);
