import React from 'react';
import { Field, reduxForm } from 'redux-form';

// validators
import { required,
    maxLengthCreator,
    minLengthCreator }
from '../../common/validators/validators';

// components
import Messages from './Messages/Messages';
import { Textarea } from '../../common/form-controls/form-controls';

import styles from './Dialogs.module.css';

const maxLength15 = maxLengthCreator(15);
const minLength2 = minLengthCreator(2);

function formDialogs(props) {
    return (
        <form className={styles.textarea_wrap} onSubmit={props.handleSubmit}>
            <Field
                component={Textarea}
                placeholder="tell me about you"
                name="message"
                validate={[required, maxLength15, minLength2]}
            />
            <button className={styles.button}>add message</button>
        </form>
    )
}

const FormDialogsRedux = reduxForm({
    form: 'formDialogs'
})(formDialogs);

function Dialogs(props) {

    const addMessage = (value) => {
        props.addMessage(value.message)
    }

    const messages = props.dialogs.messagesData.map((m, i) => {
        return <Messages key={i} message={m.message}></Messages>
    })

    return (
        <div>
            <h1 className={styles.title}>Диалоги</h1>
            <div className={styles.dialogs}>
                <div className={styles.wrap}>
                    {dialogsUsers}
                </div>
                <div className={styles.wrap}>
                    {messages}
                    <FormDialogsRedux onSubmit={addMessage} />
                </div>
            </div>
        </div>
    )
}

export default Dialogs;
