const ADD_MESSAGE_DIALOGS = 'ADD-MESSAGE';

let initialState = {
    dialogsData: [
        { id: 1, name: 'Ванес' },
        { id: 2, name: 'Валера' },
        { id: 3, name: 'Димас' },
        { id: 4, name: 'Гена' },
        { id: 5, name: 'Лёха' }
    ],

    messagesData: [
        { message: 'YO YO YO' },
        { message: 'URA URA URA' },
        { message: 'Mother Fuck' }
    ]
}

const dialogsReducer = (state = initialState, action) => {

    switch (action.type) {

        case ADD_MESSAGE_DIALOGS: {
            return {
                ...state,
                messagesData: [...state.messagesData, { message: action.message }]
            }
        }

        default:

            return state
    }
}

export default dialogsReducer;

export const addMessage = (message) => {
    return {
        type: ADD_MESSAGE_DIALOGS,
        message: message
    }
}
