import React from 'react';
import { Field, reduxForm } from 'redux-form';
import DialogUser from './DialogUser/DialogUser';
import Messages from './Messages/Messages';
import s from './Dialogs.css';
import { required,
    maxLengthCreator,
    minLengthCreator }
from '../../common/validators/validators';
import { Textarea } from '../../common/form-controls/form-controls';

const maxLength15 = maxLengthCreator(15);
const minLength2 = minLengthCreator(2);

const formDialogs = (props) => {
    return (
        <form className={s.textarea_wrap} onSubmit={props.handleSubmit}>
            <Field
                component={Textarea}
                placeholder="tell me about you"
                name="message"
                validate={[required, maxLength15, minLength2]}
            />
            <button className={s.button}>add message</button>
        </form>
    )
}

const FormDialogsRedux = reduxForm({
    form: 'formDialogs'
})(formDialogs)

const Dialogs = (props) => {

    const addMessage = (value) => {
        props.addMessage(value.message)
    }

    let dialogsUsers = props.dialogsPage.dialogsData.map((d, i) => {
        return <DialogUser id={d.id} name={d.name} key={i}></DialogUser>
    })

    let messages = props.dialogsPage.messagesData.map((m, i) => {
        return <Messages key={i} message={m.message}></Messages>
    })

    return (
        <div>
            <h1 className={s.title}>Диалоги</h1>
            <div className={s.dialogs}>
                <div className={s.wrap}>
                    {dialogsUsers}
                </div>
                <div className={s.wrap}>
                    {messages}
                    <FormDialogsRedux onSubmit={addMessage} />
                </div>
            </div>
        </div>
    )
}

export default Dialogs;
