import React from 'react';
import { Field, reduxForm } from 'redux-form';
import Post from './Post/Post';
import s from './MyPosts.module.css';
import { required,
         maxLengthCreator,
         minLengthCreator }
from '../../../common/validators/validators';
import { Textarea } from '../../../common/form-controls/form-controls';

const maxLength15 = maxLengthCreator(15);
const minLength2 = minLengthCreator(2);

const formPosts = (props) => {
    return (
        <form className={s.form} onSubmit={props.handleSubmit}>
            <Field name="message"
                component={Textarea}
                placeholder="your news"
                validate={[required, maxLength15, minLength2]} />
            <button className={s.button}>Опубликовать</button>
        </form>
    )
}

const FormPostsRedux = reduxForm({
    form: 'formPosts'
})(formPosts)

const MyPosts = (props) => {

    let posts = props.posts.postData.map((p, i) => {
        return <Post desc={p.desc} likesCounter={p.likesCounter} key={i} />
    })

    const addMessage = (value) => {
        props.addMessage(value.message);
    }

    return (
        <div className={s.wrapper}>
            <h2>Компонента MyPosts</h2>
            <div className="posts">
                <ul className={s.list}>
                    {posts}
                </ul>
                <FormPostsRedux onSubmit={addMessage} />
            </div>
        </div>
    )
}

export default MyPosts
