import React, { useEffect, useCallback } from 'react';
import { connect, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { compose } from 'redux';

// hoc
import { WithAuthRedirect } from '../../hoc/withAuthRedirect';

// actions
import { getProfileThunk,
    getUserStatusThunk,
    updateStatusThunk
} from '../../state/profile/actions';

// components
import Profile from './Profile/Profile';

function ProfileContainer(props) {
    const dispatch = useDispatch();

    const handleUserId = useCallback(() => {
        console.log('handleUserId', props)
        // let userId = props.match.params.userId;
        // if (!userId) {
        //     userId = props.autorizedUserId;

        //     if (!userId) {
        //         props.push('/login');
        //     }
        // }

        // return userId;

        dispatch(getProfileThunk('6018'));
        dispatch(getUserStatusThunk('6018'));
    }, [dispatch])

    useEffect(()=> {
        handleUserId();
    }, [handleUserId]);

    return (
        <Profile {...props}
                profile={props.profile}
                status={props.status}
                updateStatus={props.updateStatusThunk}
        />
    )
}

const mapStateToProps = (state) => {
    return {
        profile: state.profile.profile,
        status: state.profile.status,
        autorizedUserId: state.auth.userId,
        isAuth: state.auth.isAuth
    }
}

export default compose(
    connect(mapStateToProps, { updateStatusThunk }),
    withRouter,
    WithAuthRedirect
)(ProfileContainer);
