import React from 'react';
import { NavLink } from 'react-router-dom';
import { useDispatch } from 'react-redux';

// components
import Paginator from '../../Paginator';

// actions
import {
    deleteFriendThunk,
    addFriendThunk
} from '../../../state/users/actions';

// pic
import userPhoto from '../../../assets/images/user.png';

import styles from './Users.module.css';

function Users({
    totalCount,
    pageSize,
    currentPage,
    users,
    subscribersList,
}) {

    const dispatch = useDispatch();

    return (
        <div className={styles.users}>
            <Paginator totalCount={totalCount} pageSize={pageSize} currentPage={currentPage} />

            <ul className={styles.user__list}>
                {
                    users.map(u =>
                    <li key={u.id} className={styles.user__cell}>
                        <NavLink to={"/profile/" + u.id}>
                            <img src={u.photos.large ? u.photos.large : userPhoto} className={styles.img} alt="" />
                            <span>{u.status}</span>
                            <span>{u.name}</span>
                        </NavLink>

                        <div>
                            {
                                u.followed ?
                                    <button
                                        disabled={subscribersList.some(id => id === u.id)}
                                        className={styles.button}
                                        onClick={() => {
                                            dispatch(deleteFriendThunk(u.id))
                                        }}>
                                            delete friend
                                    </button>
                                    :
                                    <button
                                         disabled={subscribersList.some(id => id === u.id)}
                                         className={styles.button} onClick={() => {
                                            dispatch(addFriendThunk(u.id))
                                    }}>
                                        add friend
                                    </button>
                            }
                        </div>
                    </li>)
                }
            </ul>
        </div>
    )
}

export default Users;
