import React from 'react';
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';

// actions
import { loginOutThunk } from '../../state/auth/actions';

import styles from './Header.module.css';

function Header({isAuth, login}) {
    return (
        <header className={styles.header}>
            <img src="https://www.marketbeat.com/logos/bp-plc-logo.png" alt=""/>
            {isAuth ?
            <div>
                <div>{login}</div>

                <button className={styles.button} onClick={loginOutThunk}>Вылогиниться</button>
            </div> :

            <NavLink to="/login">
                Login
            </NavLink>}
        </header>
    );
}

const mapStateToProps = (state) => {
    return {
        isAuth: state.auth.isAuth,
        login: state.auth.login,
    }
}

export default connect(mapStateToProps, { loginOutThunk })(Header);
