// actions
import { usersAPI } from '../../api/api';

// types
import types from './types';

export const acceptAddFriend = (userId) => {
    return {
        type: types.ADD_FRIEND,
        userId
    }
}

export const acceptDeleteFriend = (userId) => {
    return {
        type: types.DELETE_FRIEND,
        userId
    }
}

export const setUsers = (users) => {
    return {
        type: types.SET_USERS,
        users
    }
}

export const setCurrentPage = (currentPage) => {
    return {
        type: types.SET_CURRENT_PAGE,
        currentPage
    }
}

export const setTotalCount = (totalCount) => {
    return {
        type: types.SET_TOTAL_COUNT,
        totalCount
    }
}

export const setToggleIsFetching = (isFetching) => {
    return {
        type: types.TOGGLE_IS_FETCHING,
        isFetching
    }
}

export const setWaitIsSubscribe = (isFetching, userId) => {
    return {
        type: types.TOGGLE_IS_WAIT_SUBSCRIBE,
        isFetching,
        userId
    }
}

export const getUsersThunk = (currentPage, pageSize) => {
    return (dispatch) => {
        dispatch(setToggleIsFetching(true));

        usersAPI.getUsers(currentPage, pageSize)
            .then(data => {
                dispatch(setToggleIsFetching(false));
                dispatch(setUsers(data.items));
                dispatch(setTotalCount(data.totalCount));
                dispatch(setCurrentPage(currentPage))
            })
    }
}

export const deleteFriendThunk = (userId) => {

    return (dispatch) => {
        dispatch(setWaitIsSubscribe(true, userId))
        usersAPI.deleteFriend(userId).then(data => {
            if (data.resultCode === 0) {
                dispatch(acceptDeleteFriend(userId))
            }
            dispatch(setWaitIsSubscribe(false, userId))
        })
    }
}

export const addFriendThunk = (userId) => {

    return (dispatch) => {
        dispatch(setWaitIsSubscribe(true, userId))
        usersAPI.addFriend(userId).then(data => {
            if (data.resultCode === 0) {
                dispatch(acceptAddFriend(userId))
            }
            dispatch(setWaitIsSubscribe(false, userId))
        })
    }
}
