import React from 'react';
import PropTypes from 'prop-types';
import cn from 'classnames';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';

// actions
import { loginThunk } from '../../state/auth/actions';

// components
import FormLoginRedux from './Form';

import styles from './Login.module.css';

function login({ className, isAuth, loginThunk }) {
    //const dispatch = useDispatch();

    const onSubmit = (formData) => {
        loginThunk(formData.email, formData.password, formData.rememberMe);
    }

    if (isAuth) {
        return <Redirect to="/profile" />
    }

    return <div className={cn(styles.Root, className)}>
        <h1>Это логин</h1>

        <FormLoginRedux onSubmit={onSubmit} />
    </div>
}

const mapStateToProps = (state) => {
    return {
        isAuth: state.auth.isAuth
    }
}

login.propTypes = {
    className: PropTypes.string,
    isAuth: PropTypes.bool.isRequired,
    loginThunk: PropTypes.func.isRequired,
}

login.defaultProps = {
    className: '',
}

export default connect(mapStateToProps, { loginThunk })(login);
