import React, { useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';

// actions
import {
    deleteFriendThunk,
    addFriendThunk,
    getUsersThunk,
} from '../../state/users/actions';

// components
import Preloader from '../common/Preloader/Preloader';
import Paginator from '../Paginator';

// pic
import userPhoto from '../../assets/images/user.png';

import styles from './Users.module.css';

function Users() {
    const dispatch = useDispatch();
    const pageSize = useSelector(state => state.users.pageSize);
    const currentPage = useSelector(state => state.users.currentPage);
    const users = useSelector(state => state.users.users);
    const totalCount = useSelector(state => state.users.totalCount);
    const isFetching = useSelector(state => state.users.isFetching);
    const subscribersList = useSelector(state => state.users.subscribersList);

    useEffect(() => {
        dispatch(getUsersThunk(currentPage, pageSize));
    }, [currentPage, dispatch, pageSize])

    if (isFetching) {
        return <Preloader />
    }

    return (
        <div className={styles.users}>
            <Paginator totalCount={totalCount} pageSize={pageSize} currentPage={currentPage} />

            <ul className={styles.user__list}>
                {
                    users.map(u =>
                        <li key={u.id} className={styles.user__cell}>
                            <NavLink to={"/profile/" + u.id}>
                                <img src={u.photos.large ? u.photos.large : userPhoto} className={styles.img} alt="" />
                                <span>{u.status}</span>
                                <span>{u.name}</span>
                            </NavLink>

                            <div>
                                {
                                    u.followed ?
                                        <button
                                            disabled={subscribersList.some(id => id === u.id)}
                                            className={styles.button}
                                            onClick={() => {
                                                dispatch(deleteFriendThunk(u.id))
                                            }}>
                                            delete friend
                                </button>
                                        :
                                        <button
                                            disabled={subscribersList.some(id => id === u.id)}
                                            className={styles.button} onClick={() => {
                                                dispatch(addFriendThunk(u.id))
                                            }}>
                                            add friend
                                </button>
                                }
                            </div>
                        </li>)
                }
            </ul>
        </div>
    )
}

export default Users;
