function ProfileContainer(props) {
    const dispatch = useDispatch();

    const handleUserId = useCallback(() => {
        let userId = props.match.params.userId;
        if (!userId) {
            userId = props.autorizedUserId;

            if (!userId) {
                props.push('/login');
            }
        }
    }, [props])

    useEffect(()=> {
        console.log('props', props)
        dispatch(getProfileThunk(handleUserId()));
        dispatch(getUserStatusThunk(handleUserId()));
    }, [dispatch, handleUserId]);

    return (
        <>
            {console.log('return')}
            <Profile {...props} profile={props.profile}
                    status={props.status}
                    updateStatus={props.updateStatusThunk}
            />
        </>
    )
}

const mapStateToProps = (state) => {
    return {
        profile: state.profile.profile,
        status: state.profile.status,
        autorizedUserId: state.auth.userId,
        isAuth: state.auth.isAuth
    }
}

export default compose(
    connect(mapStateToProps, { updateStatusThunk }),
    withRouter,
    WithAuthRedirect
)(ProfileContainer);
