import React, { useMemo } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Field, reduxForm } from 'redux-form';

// validators
import { required,
    maxLengthCreator,
    minLengthCreator
} from '../../../common/validators/validators';

// components
import { Textarea } from '../../../common/form-controls/form-controls';
import Post from './Post/Post';

// actions
import { addMessage } from '../../../../state/profile/actions';

import styles from './MyPosts.module.css';

const maxLength15 = maxLengthCreator(15);
const minLength2 = minLengthCreator(2);

function formPosts(props) {
    return (
        <form className={styles.form} onSubmit={props.handleSubmit}>
            <Field name="message"
                component={Textarea}
                placeholder="your news"
                validate={[required, maxLength15, minLength2]} />
            <button className={styles.button}>Опубликовать</button>
        </form>
    )
}

const FormPostsRedux = reduxForm({
    form: 'formPosts'
})(formPosts);

function MyPosts() {
    const postsTest = useSelector(state => state.profile.posts);
    const dispatch = useDispatch();

    // const posts = props.posts.postData.map((p, i) => {
    //     return <Post desc={p.desc} likesCounter={p.likesCounter} key={i} />
    // })

    const postItem = useMemo(postsTest.postData.map((p, i) => {
        return <Post desc={p.desc} likesCounter={p.likesCounter} key={i} />
    }))

    const sendMessage = (value) => {
        dispatch(addMessage(value.message));
    }

    return (
        <div className={styles.wrapper}>
            <h2>Компонента MyPosts</h2>

            <div className="posts">
                <ul className={styles.list}>
                    {postItem}
                </ul>

                <FormPostsRedux onSubmit={sendMessage} />
            </div>
        </div>
    )
}

export default MyPosts;
