import React, { useEffect, useCallback } from 'react';
import { connect, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { compose } from 'redux';

// hoc
import { WithAuthRedirect } from '../../hoc/withAuthRedirect';

// actions
import { getProfileThunk,
    getUserStatusThunk,
    updateStatusThunk
} from '../../state/profile/actions';

// components
import Profile from './Profile/Profile';

function ProfileContainer({match, autorizedUserId, profile, status, updateStatusThunk}) {
    const dispatch = useDispatch();

    const handleUserId = useCallback(() => {
        let userId = match.params.userId;
        if (!userId) {
            userId = autorizedUserId;

            // if (!userId) {
            //     props.push('/login');
            // }
        }

        dispatch(getProfileThunk(userId));
        dispatch(getUserStatusThunk(userId));
    }, [autorizedUserId, dispatch, match.params.userId])

    useEffect(()=> {
        handleUserId();
    }, [handleUserId]);

    return (
        <Profile
                profile={profile}
                status={status}
        />
    )
}

const mapStateToProps = (state) => {
    return {
        profile: state.profile.profile,
        status: state.profile.status,
        autorizedUserId: state.auth.userId,
        isAuth: state.auth.isAuth,
    }
}

export default compose(
    connect(mapStateToProps, { updateStatusThunk }),
    withRouter,
    WithAuthRedirect
)(ProfileContainer);
