import React, { useEffect, useCallback } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { compose } from 'redux';

// hoc
import { WithAuthRedirect } from '../../hoc/withAuthRedirect';

// actions
import {
    getProfileThunk,
    getUserStatusThunk
} from '../../state/profile/actions';

// components
import MyPostsContainer from '../MyPosts/MyPostsContainer';
import ProfileInfo from '../ProfileInfo/ProfileInfo';

function ProfileContainer({ match, autorizedUserId, profile, status, getProfileThunk, getUserStatusThunk }) {

    const handleUserId = useCallback(() => {
        let userId = match.params.userId;

        if (!userId) {
            userId = autorizedUserId;
        }

        getProfileThunk(userId);
        getUserStatusThunk(userId);
    }, [autorizedUserId, getProfileThunk, getUserStatusThunk, match.params.userId])

    useEffect(() => {
        handleUserId();
    }, [handleUserId]);

    return (
        <div>
            <ProfileInfo
                profile={profile}
                status={status} />

            <MyPostsContainer />
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        profile: state.profile.profile,
        status: state.profile.status,
        autorizedUserId: state.auth.userId,
        isAuth: state.auth.isAuth,
    }
}

export default compose(
    connect(mapStateToProps, { getProfileThunk, getUserStatusThunk }),
    withRouter,
    WithAuthRedirect
)(ProfileContainer);
