// actions
import { stopSubmit } from 'redux-form';
import { authMeAPI } from '../../api/api';

// types
import types from './types';

export const setAuthUserData = (login, email, userId, isAuth) => {
    return {
        type: types.SET_AUTH_DATA_USER,
        data: {
            login,
            email,
            userId,
            isAuth
        }
    }
}

export const getMeThunk = () => {
    return (dispatch) => {
        return authMeAPI.getAuthMe().then(data => {
            if (data.resultCode === 0) {
                const { login, email, id, } = data.data;
                dispatch(setAuthUserData(login, email, id, true));
            }
        })
    }
}

export const loginThunk = (email, password, rememberMe) => {
    return (dispatch) => {
        authMeAPI.login(email, password, rememberMe).then(data => {
            if (data.resultCode === 0) {
                dispatch(getMeThunk());
            } else {
                const message = data.messages
                dispatch(stopSubmit('formLogin', {_error: message ? message : 'some wrong' }));
            }
        })
    }
}

export const loginOutThunk = () => {
    return (dispatch) => {
        authMeAPI.loginOut().then(data => {
            if (data.resultCode === 0) {
                dispatch(setAuthUserData(null, null, null, false));
            }
        })
    }
}
