import types from './types';
import { getMeThunk } from '../../redux/auth-reducer';
//import { initializedSuccess } from './actions';

// initial state
const initialState = {
    initialized: false,
}

// reducer
export default function reducer(state = initialState, action) {
    switch (action.type) {
        case types.INITIALIZED_SUCCESS: {
            return {
                ...state,
                initialized: true
            }
        }
        default: {
            return state
        }
    }
}

const initializedSuccess = () => {
    return { type: types.INITIALIZED_SUCCESS }
}

console.log('types.INITIALIZED_SUCCESS', types.INITIALIZED_SUCCESS)

export const getInitializeThunk = () => {
    return (dispatch) => {
        let promise = dispatch(getMeThunk())
        promise.then(() => {
           dispatch(initializedSuccess())
        })
    }
}
