import React from 'react';
import { NavLink } from 'react-router-dom';

// pic
import userPhoto from '../../../assets/images/user.png';

import styles from './Users.module.css';

function Users({totalCount, pageSize, currentPage, onPageChanged, users, subscribersList, deleteFriendThunk, addFriendThunk}) {
    const pageCount = Math.ceil(totalCount / pageSize)

    const pages = [];

    for (let i = 1; i <= pageCount; i++) {
        pages.push(i)
    }

    return (
        <div className={styles.users}>
            <ul className={styles.users__pagination}>
                {
                    pages.map((p, id) => {
                        return <li key={id} className={
                            currentPage === p ? styles.users__pagination__cell + ' ' +
                                styles.selected :
                                styles.users__pagination__cell}
                            onClick={() => {
                                onPageChanged(p)
                            }}>{p}</li>
                    })
                }
            </ul>

            <ul className={styles.user__list}>
                {
                    users.map(u =>
                    <li key={u.id} className={styles.user__cell}>
                        <NavLink to={"/profile/" + u.id}>
                            <img src={u.photos.large ? u.photos.large : userPhoto} className={styles.img} alt="" />
                            <span>{u.status}</span>
                            <span>{u.name}</span>
                        </NavLink>

                        <div>
                            {
                                u.followed ?
                                    <button
                                        disabled={subscribersList.some(id => id === u.id)}
                                        className={styles.button}
                                        onClick={() => {
                                            deleteFriendThunk(u.id)
                                        }}>
                                            delete friend
                                    </button>
                                    :
                                    <button
                                         disabled={subscribersList.some(id => id === u.id)}
                                         className={styles.button} onClick={() => {
                                         addFriendThunk(u.id)
                                    }}>
                                        add friend
                                    </button>
                            }
                        </div>
                    </li>)
                }
            </ul>
        </div>
    )
}

export default Users;
