import React from 'react';
import { Field, reduxForm } from 'redux-form';
import { connect } from 'react-redux';
import { loginThunk } from '../../redux/auth-reducer';
import { Input } from '../common/form-controls/form-controls';
import {
    required,
    maxLengthCreator,
    minLengthCreator
}
    from '../common/validators/validators';
import s from './Login.css';
import { Redirect } from 'react-router-dom';

const maxLength100 = maxLengthCreator(100);
const minLength5 = minLengthCreator(5);

const LoginForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit} className={s.form}>
            <Field component={Input}
                name="email"
                placeholder="Введите email"
                type="email"
                className={s.field}
                validate={[required, maxLength100, minLength5]}>
            </Field>
            <Field component={Input}
                name="password"
                placeholder="Введите пароль"
                type="password"
                className={s.field}
                validate={[required, maxLength100, minLength5]}>
            </Field>
            <Field component={Input}
                name="rememberMe"
                type="checkbox"
                className={s.checkbox}
                validate={[required, maxLength100, minLength5]}>
            </Field>
            {props.error && <span className={s.errorNote}>{props.error}</span>}
            <button className={s.button}>Зарегаться</button>
        </form>
    )
}

const FormLoginRedux = reduxForm({
    form: 'formLogin'
})(LoginForm)

const Login = (props) => {

    if (props.isAuth) {
        return <Redirect to="/profile" />
    }

    console.log('Login', props)
    const onSubmit = (formData) => {
        props.loginThunk(formData.email, formData.password, formData.rememberMe)
        console.log('formData', formData.email, formData.password, formData.rememberMe)
    }
    return <div>
        <h1>Это логин</h1>
        <FormLoginRedux onSubmit={onSubmit} />
    </div>
}

const mapStateToProps = (state) => {
    return {
        isAuth: state.auth.isAuth
    }
}

export default connect(mapStateToProps, { loginThunk })(Login)
