import React from 'react';
import PropTypes from 'prop-types';
import cn from 'classnames';
import { connect, useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';

// actions
import { loginOutThunk } from '../../state/auth/actions';

import styles from './Header.module.css';

function Header({ className, isAuth, login }) {
    const dispatch = useDispatch();

    const loginOut = () => {
        dispatch(loginOutThunk());
    }

    return (
        <header className={cn(styles.Root, className)}>
            <img src="https://www.marketbeat.com/logos/bp-plc-logo.png" alt=""/>

            {isAuth ?
                <div>
                    <div>{login}</div>
                    <button className={cn(styles.button)} onClick={loginOut}>Вылогиниться</button>
                </div> :

                <NavLink to="/login">
                    Login
                </NavLink>
            }
        </header>
    );
}

Header.propTypes = {
    className: PropTypes.string,
    isAuth: PropTypes.bool.isRequired,
    login: PropTypes.string,
};

Header.defaultProps = {
    className: '',
    login: 'Login',
};

const mapStateToProps = (state) => {
    return {
        isAuth: state.auth.isAuth,
        login: state.auth.login,
    }
}

export default connect(mapStateToProps)(Header);
