import React from 'react';
import s from './Users.module.css';
import userPhoto from '../../../assets/images/user.png';
import { NavLink } from 'react-router-dom';

const Users = (props) => {
    const pageCount = Math.ceil(props.totalCount / props.pageSize)

    const pages = [];

    for (let i = 1; i <= pageCount; i++) {
        pages.push(i)
    }

    return (
        <div className={s.users}>
            <ul className={s.users__pagination}>
                {
                    pages.map(p => {
                        return <li key={p.id} className={
                            props.currentPage === p ? s.users__pagination__cell + ' ' +
                                s.selected :
                                s.users__pagination__cell}
                            onClick={() => {
                                props.onPageChanged(p)
                            }}>{p}</li>
                    })
                }
            </ul>
            <ul className={s.user__list}>
                {
                    props.users.map(u => 
                    <li key={u.id} className={s.user__cell}>
                        <NavLink to={"/profile/" + u.id}>
                            <img src={u.photos.large ? u.photos.large : userPhoto} className={s.img} />
                            <span>{u.status}</span>
                            <span>{u.name}</span>
                        </NavLink>
                        <div>
                            {
                                u.followed ?
                                    <button 
                                        disabled={props.subscribersList.some(id => id === u.id)} 
                                        className={s.button} 
                                        onClick={() => {
                                            props.deleteFriendThunk(u.id)
                                        }}>
                                            delete friend
                                    </button> 
                                    :
                                    <button 
                                         disabled={props.subscribersList.some(id => id === u.id)} 
                                         className={s.button} onClick={() => {
                                         props.addFriendThunk(u.id)
                                    }}>
                                        add friend
                                    </button>
                            }
                        </div>
                    </li>)
                }
            </ul>
        </div>
    )
}

export default Users;
