import React from 'react';
import PropTypes from 'prop-types';
import cn from 'classnames';
import { connect } from 'react-redux';
import { compose } from 'redux';

// hoc
import { WithAuthRedirect } from '../../hoc/withAuthRedirect';

// actions
import { addMessage } from '../../state/dialogs/actions';

// components
import Messages from './Messages/Messages';
import FormDialogsRedux from './Form/Form';

import styles from './Dialogs.module.css';

function Dialogs(props) {

    const sendMessage = (value) => {
        props.addMessage(value.message)
    }

    const messages = props.messagesDefault.map((m, i) => {
        return <Messages key={i} message={m.message}></Messages>
    })

    return (
        <div className={cn(styles.Root)}>
            <h1 className={cn(styles.title)}>Диалоги</h1>
            <div className={cn(styles.dialogs)}>
                <div className={cn(styles.wrap)}>
                    {messages}
                    <FormDialogsRedux onSubmit={sendMessage} />
                </div>
            </div>
        </div>
    )
}

Dialogs.propTypes = {
    messagesDefault: PropTypes.arrayOf(
        PropTypes.shape({
            message: PropTypes.string.isRequired,
        }),
    ).isRequired,
    addMessage: PropTypes.func
};

Dialogs.defaultProps = {
    addMessage: () => {},
};

const mapStateToProps = (state) => {
    return {
        messagesDefault: state.dialogs.messagesData,
    }
}

export default compose(
    connect(mapStateToProps, { addMessage }),
    WithAuthRedirect
)(Dialogs);
