// api
import { instanceAxios } from '../../utils/api';
import profileAPI from '../profile/api';

// constants
import { STYLE_CONSOLE } from '../../constants/constants';

export const usersAPI = {
    getUsers(currentPage, pageSize) {
        return instanceAxios.get(`users?page=${currentPage}&count=${pageSize}`)
            .then(response => {
                return response.data
            })
    },

    addFriend(id) {
        return instanceAxios.post(`follow/${id}`)
            .then(response => {
                return response.data
            })
    },

    deleteFriend(id) {
        return instanceAxios.delete(`follow/${id}`)
            .then(response => {
                return response.data
            })
    },

    getProfile(userId) {
        console.log('%cObsolete method! Please use profileAPI', STYLE_CONSOLE);
        return profileAPI.getProfile(userId);
    }
}
