function ProfileContainer(props) {
    useEffect(()=> {
        console.log('useEffect Profile Container', props);
        let userId = props.match.params.userId;
        if (!userId) {
            userId = props.autorizedUserId;

            if (!userId) {
                props.push('/login');
            }
        }
        props.getProfileThunk(userId);
        props.getUserStatusThunk(userId);
    }, [props]);

    return (
        <Profile {...props} profile={props.profile}
                status={props.status}
                updateStatus={props.updateStatusThunk}
        />
    )
}

const mapStateToProps = (state) => {
    return {
        profile: state.profile.profile,
        status: state.profile.status,
        autorizedUserId: state.auth.userId,
        isAuth: state.auth.isAuth
    }
}

export default compose(
    connect(mapStateToProps, { getProfileThunk, getUserStatusThunk, updateStatusThunk }),
    withRouter,
    WithAuthRedirect
)(ProfileContainer);
