import React from 'react';
import { Field, reduxForm } from 'redux-form';

// components
import { Textarea } from '../common/form-controls/form-controls';

// validators
import { required,
    maxLengthCreator,
    minLengthCreator }
from '../common/validators/validators';

import styles from './Dialogs.module.css';

const maxLength15 = maxLengthCreator(15);
const minLength2 = minLengthCreator(2);

function formDialogs(props) {
    return (
        <form className={styles.textarea_wrap} onSubmit={props.handleSubmit}>
            <Field
                component={Textarea}
                placeholder="tell me about you"
                name="message"
                validate={[required, maxLength15, minLength2]}
            />
            <button className={styles.button}>add message</button>
        </form>
    )
}

const FormDialogsRedux = reduxForm({
    form: 'formDialogs'
})(formDialogs);

export default FormDialogsRedux;
