import React from 'react';
import Profile from './Profile/Profile';
import { getProfileThunk,
         getUserStatusThunk,
         updateStatusThunk
} from '../../redux/profile-reducer';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { WithAuthRedirect } from '../../hoc/withAuthRedirect';
import { compose } from 'redux';


class ProfileContainer extends React.Component {
    componentDidMount() {
        console.log('profilecontainer', this.props);
        let userId = this.props.match.params.userId;
        if (!userId) {
            userId = this.props.autorizedUserId;
            console.log('this.props.autorizedUserId', this.props.autorizedUserId)

            if (!userId) {
                this.props.push('/login');
            }
        }
        this.props.getProfileThunk(userId);
        this.props.getUserStatusThunk(userId);
    }

    render() {
        return (
            <Profile {...this.props}
                    profile={this.props.profile}
                    status={this.props.status}
                    updateStatus={this.props.updateStatusThunk} />
        )
    }
}

const mapStateToProps = (state) => {
    return {
        profile: state.profilePage.profile,
        status: state.profilePage.status,
        autorizedUserId: state.auth.userId,
        isAuth: state.auth.isAuth
    }
}

export default compose(
    connect(mapStateToProps, { getProfileThunk, getUserStatusThunk, updateStatusThunk }),
    withRouter,
    WithAuthRedirect
)(ProfileContainer);
