// api
import { authMeAPI, securityAPI } from './api';

// actions
import { stopSubmit } from 'redux-form';

// types
import types from './types';

export function setAuthUserData(login, email, userId, isAuth) {
    return {
        type: types.SET_AUTH_DATA_USER,
        data: {
            login,
            email,
            userId,
            isAuth
        }
    }
}

export function getCaptchaSuccess(captcha) {
    return {
        type: types.GET_CAPTCHA_SUCCESS,
        data: {
            captcha
        }
    }
}

export function getMeThunk() {
    return (dispatch) => {
        return authMeAPI.getAuthMe().then(data => {
            if (data.resultCode === 0) {
                const { login, email, id, } = data.data;
                dispatch(setAuthUserData(login, email, id, true));
            }
        })
    }
}

export function loginThunk(email, password, rememberMe) {
    return (dispatch) => {
        authMeAPI.login(email, password, rememberMe).then(data => {
            if (data.resultCode === 0) {
                dispatch(getMeThunk());
            } else if (data.resultCode === 10) {
                dispatch()
            }
            else {
                const message = data.messages
                dispatch(stopSubmit('formLogin', {_error: message ? message : 'some wrong' }));
            }
        })
    }
}

export function loginOutThunk() {
    return (dispatch) => {
        authMeAPI.loginOut().then(data => {
            if (data.resultCode === 0) {
                dispatch(setAuthUserData(null, null, null, false));
            }
        })
    }
}

export function getCaptchaThunk() {
    return (dispatch) => {
        securityAPI.getCaptcha().then(data => {
            dispatch(setAuthUserData(data.captcha));
        })
    }
}
